-- WorldPvP Loot Table Data
-- Generated automatically from item_template

CREATE TABLE IF NOT EXISTS worldpvp_loot (
    id INT AUTO_INCREMENT PRIMARY KEY,
    entry INT NOT NULL COMMENT 'Item template entry ID',
    iLevel INT NOT NULL COMMENT 'Item level',
    quality INT NOT NULL COMMENT 'Item quality (2=Uncommon, 3=Rare, 4=Epic)',
    name VARCHAR(255) NOT NULL COMMENT 'Item name',
    INDEX idx_entry (entry),
    INDEX idx_ilevel (iLevel),
    INDEX idx_quality (quality),
    INDEX idx_composite (quality, iLevel)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='World PvP loot rewards table';

